import React, { useRef } from 'react';
import ReactDOM from 'react-dom'; 
import * as tf from "@tensorflow/tfjs";
import * as bodyPix from "@tensorflow-models/body-pix";
import Webcam from "react-webcam";
import './App.css';
import menuStyles from './Menu.module.css';
import backVideo from './airport.mp4';

const root = document.getElementById('root');

function VideoFrame () {
	const webcamRef = useRef(null);
	const canvasRef = useRef(null);
	const backVideoRef = useRef(null);
	let render = false;
	let currentMask;

	const dimensions = [window.innerWidth, window.innerHeight];
	console.log(dimensions);

	const runBodySegment = async () => {
		render = true;
		const net = await bodyPix.load();
		if (backVideoRef.current) {
			backVideoRef.current.play();
		}
		const interval = setInterval(() => {
			if (render) {
				detect(net);
			} else {
				clearInterval(interval);
			}
		}, 70);
	}
	const backMask = (image, mask) => {
		const cv = document.createElement('canvas');
		cv.width = dimensions[0];
		cv.height = dimensions[1];
		const ctx = cv.getContext('2d');
		ctx.drawImage(backVideoRef.current, 0, 0, cv.width, cv.height);
		const imageData = ctx.getImageData(0, 0, cv.width, cv.height);
		// console.log(image, mask);
		for (let i = 0; i < image.data.length; i+=4) {
			const vector = [
				mask.data[i],
				mask.data[i + 1],
				mask.data[i + 2],
				mask.data[i + 3],
			]
			if (
				vector[0] === 255 &&
				vector[1] === 255 &&
				vector[2] === 255
				) {
					image.data[i] = imageData.data[i];
					image.data[i + 1] = imageData.data[i + 1];
					image.data[i + 2] = imageData.data[i + 2];
					image.data[i + 3] = imageData.data[i + 3];
			}
		}
		return image;
	}

	const detect = async (net) => {
		if (
			typeof webcamRef.current !== "undefined" &&
			webcamRef.current !== null &&
			webcamRef.current.video.readyState === 4
			) {
				const video = webcamRef.current.video;
				const videoHeight = video.videoHeight;
				const videoWidth = video.videoWidth;

				webcamRef.current.video.width = videoWidth;
				webcamRef.current.video.height = videoHeight;
				canvasRef.current.width = videoWidth;
				canvasRef.current.height = videoHeight;

				const outStride = 16;
				const segThreshold = 0.5;
				const cv = document.createElement('canvas');
				cv.width = dimensions[0];
				cv.height = dimensions[1];
				const ctx = cv.getContext('2d');
				ctx.drawImage(video, 0, 0, cv.width, cv.height);
				const imageData = ctx.getImageData(0, 0, cv.width, cv.height);
				const person = await net.segmentPersonParts(video, outStride, segThreshold);
				const coloredPart = bodyPix.toColoredPartMask(person);
				const back = backMask(imageData, coloredPart);
				const ctx2 = canvasRef.current.getContext('2d');
				ctx2.putImageData(back, 0, 0);
			}
	}
	const stop = () => {
		backVideoRef.current.pause();
		render = false;
	}
	navigator.getUserMedia({audio:true, video:true}, function () {
		console.log('has permission');
	}, err => {
		console.log(err);
	});
	runBodySegment();
	const videoConstraints = {
		width: dimensions[0],
		height: dimensions[1]
	}
	return (
			<div>
			<Webcam ref={webcamRef}
					style = {{
						position:"absolute",
						marginLeft:0,
						marginRight:0,
						left:0,
						right:0,
						textAlign:"center",
						zIndex:9,
						width:dimensions[0],
						height:dimensions[1],
						objectFit:"cover"
					}}
					videoConstraints={ videoConstraints }
					/>
				<canvas ref={canvasRef}
					style = {{
						position:"absolute",
						left:0,
						right:0,
						textAlign:"center",
						zIndex:9,
						width:"100%",
						height:"100%",
						backgroundColor: "transparent"
					}} />
				<video 
					ref={ backVideoRef }
					style = {{
						visibility: "hidden",
						position:"absolute",
						left:0,
						right:0,
						textAlign:"center",
						zIndex:8,
						width:"100%",
						height:"100%",
						objectFit:"cover"
					}}
					>
					<source src={ backVideo } type="video/mp4"/>
				</video>
				<div className="controls">
					<button onClick={()=> {runBodySegment()} }>start</button>
					<button onClick={()=> {stop()}}>stop</button>
					<button onClick={()=> {}}>record</button>
					<button onClick={()=> {}}>save</button>
				</div>
			</div>
		)
}

class MainView extends React.Component {
	render () {
		return (
			<div className={ menuStyles.menu }>
				<button className={ menuStyles.gallery } onClick={()=>{this.gallery()}}></button>
				<button className={ menuStyles.creator } onClick={()=>{this.creator()}}></button>
				<button className={ menuStyles.user } onClick={()=>{this.user()}}></button>
			</div>
		);
	}
	gallery () {

	}
	creator () {
		ReactDOM.render(
			(
				<VideoFrame></VideoFrame>
			),
			root
		);
	}
	user () {

	}
}

function App() {
	const renderVideoCapture = () => {
		React.render(
			(
				<VideoFrame>
				</VideoFrame>
			),
			root
		)
	};
  return (
	<MainView></MainView>
  );
}

export default App;
