import React, { useRef } from 'react';
import * as tf from "@tensorflow/tfjs";
import * as bodyPix from "@tensorflow-models/body-pix";
import Webcam from "react-webcam";
import './App.css';
import backVideo from './airport.mp4';

function App() {
	const webcamRef = useRef(null);
	const canvasRef = useRef(null);
	const backVideoRef = useRef(null);

	const runBodySegment = async () => {
		const net = await bodyPix.load();
		console.log(backVideoRef);
		if (backVideoRef.current) {
			backVideoRef.current.play();
		}
		// backVideoRef.play();
		detect(net);
		// setInterval(() => {
		// 	detect(net);
		// }, 100);
	}
	const backMask = function (image) {
		const video = backVideoRef.current;
		console.log(video);
		return image;
	}

	const detect = async (net) => {
		if (
			typeof webcamRef.current !== "undefined" &&
			webcamRef.current !== null &&
			webcamRef.current.video.readyState === 4
			) {
				const video = webcamRef.current.video;
				const videoHeight = video.videoHeight;
				const videoWidth = video.videoWidth;

				webcamRef.current.video.height = videoHeight;
				webcamRef.current.video.width = videoWidth;

				canvasRef.current.width = videoWidth;
				canvasRef.current.height = videoHeight;

				const outStride = 16;
				const segThreshold = 0.5;
				const person = await net.segmentPersonParts(video, outStride, segThreshold);
				console.log(person);

				const coloredPart = bodyPix.toColoredPartMask(person);
				const back = backMask(coloredPart);
				console.log(back);
				bodyPix.drawMask(
					canvasRef.current,
					video,
					back,
					0.7,
					0,
					false
				)
			}
	}

	runBodySegment();

  return (
    <div className="App">
      <header className="App-header">
	   <Webcam ref={webcamRef}
			style = {{
				position:"absolute",
				marginLeft:"auto",
				marginRight:"auto",
				left:0,
				right:0,
				textAlign:"center",
				zIndex:9,
				width:640,
				height:480
			}} />
		<canvas ref={canvasRef}
			style = {{
				position:"absolute",
				marginLeft:"auto",
				marginRight:"auto",
				left:0,
				right:0,
				textAlign:"center",
				zIndex:9,
				width:640,
				height:480
			}} />
		<video 
			ref={ backVideoRef }
			style = {{
				position:"absolute",
				// marginTop:0,
				marginLeft:"auto",
				marginRight:"auto",
				left:0,
				// top:0,
				opacity:0.3,
				right:0,
				textAlign:"center",
				zIndex:12,
				width:640,
				height:480,
				objectFit:"cover"
			}}
			width="640"
			height="480">
			<source src={ backVideo } type="video/mp4"/>
		</video>
      </header>
    </div>
  );
}

export default App;
